import "../../sass/pages/loginPage/LoginPage.scss";
import Form from "./loginPageForm.jsx"
import { fetch_Token } from "../../services/login.service.js";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserCircle } from "@fortawesome/free-solid-svg-icons";
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { fetch_UserInfos } from "../../services/login.service.js";
import { set_Login } from "../../Redux/authReducer/authSlice.js";
import { set_User } from "../../Redux/userReducer/userSlice.js";
import { useDispatch, useSelector } from "react-redux";
import Invalid from "./loginPageInvalid.jsx";

/**
 * Component for the login page.
 * @returns {JSX.Element} The LoginPage component.
 */
export default function LoginPage() {
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [remember, setRemember] = useState(false);
  const [show, setShow] = useState(false);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const TokenInState = useSelector((state) => state.login.token);

  useEffect(() => {
    console.log(TokenInState);
    if (TokenInState) {
      navigate("/profile");
    }
  }, [TokenInState, navigate]);




  return (
    <main className="main__LoginPage">
      <div className="main__LoginPage__Container">
        {/* User icon */}
        <FontAwesomeIcon className="title_Login_Icon" icon={faUserCircle} />
        <h1 className="main__LoginPage__Container__title">Sign In</h1>

      </div>
    </main>
  );
}
