import { useEffect, useState } from "react";
import Invalid from "./loginPageInvalid.jsx";
import { fetch_Token } from "../../services/login.service.js";

export default function Form() {
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [remember, setRemember] = useState(false);
  const [show, setShow] = useState(false);

  /**
   * Function to handle the "Remember me" checkbox.
   */
  const handleCheck = () => {
    // Get the 'remember' checkbox element
    const check = document.getElementById("remember");
    // If the checkbox is checked, set 'remember' state to true
    if (check.checked) {
      setRemember(true);
    }
  };

  /**
   * Function to handle the login form submission.
   * @param {Object} e - The form submission event.
   */
  const handle_Form = async (e) => {
    e.preventDefault();

    // Attempt to fetch a token with the provided password and email
    const token = await fetch_Token(password, email);

    // If the password or email is empty, or fetching the token fails, show an error message
    if (password === "" || email === "" || !token) {
      setShow(true);
      setTimeout(() => {
        setShow(false);
      }, 3000);
    } else {
      // If credentials are valid, dispatch the login action to update the state
      dispatch(
        set_Login({ token: token, keepLogging: remember, authenticated: true })
      );

      // Fetch user information using the token
      const infos = await fetch_UserInfos(token);
      const user = {
        firstName: infos.firstName,
        lastName: infos.lastName,
        id: infos.id,
      };

      // Dispatch the set user action to update user information in the state
      dispatch(set_User(user));

      // Navigate to the user's profile page
      navigate("/profile");
    }
    return;
  };

  return (
    <form>
      {/* Username input */}
      <div className="loginForm__Container">
        <label className="loginForm__Container__Label" htmlFor="userMail">
          Username
        </label>
        <input
          className="input"
          type="text"
          id="userMail"
          name="username"
          autoComplete="off"
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />
      </div>
      {/* Password input */}
      <div className="loginForm__Container">
        <label className="loginForm__Container__Label" htmlFor="userPassword">
          Password
        </label>
        <input
          className="input"
          type="password"
          autoComplete="off"
          id="userPassword"
          name="password"
          onChange={(e) => {
            setPassword(e.target.value);
          }}
        />
        {/* Display the Forgot component with error message if 'show' is true */}
        <Invalid show={show} />
      </div>
      {/* "Remember me" checkbox */}
      <div className="check__Container">
        <input
          className="check__Container__Input"
          type="checkbox"
          id="remember"
          name="Remember me"
          onClick={handleCheck}
        />
        <label className="check__Container__Label" htmlFor="remember">
          Remember me
        </label>
      </div>
      {/* Sign-in button */}
      <div className="loginButton">
        <button className="button" type="button" onClick={handle_Form}>
          Sign in
        </button>
      </div>
    </form>
  );
}
