import { useEffect, useState } from "react";
import Invalid from "./loginPageInvalid.jsx";

export default function Form() {
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [remember, setRemember] = useState(false);
  const [show, setShow] = useState(false);
  return (
    <form>
      {/* Username input */}
      <div className="loginForm__Container">
        <label className="loginForm__Container__Label" htmlFor="userMail">
          Username
        </label>
        <input
          className="input"
          type="text"
          id="userMail"
          name="username"
          autoComplete="off"
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />
      </div>
      {/* Password input */}
      <div className="loginForm__Container">
        <label className="loginForm__Container__Label" htmlFor="userPassword">
          Password
        </label>
        <input
          className="input"
          type="password"
          autoComplete="off"
          id="userPassword"
          name="password"
          onChange={(e) => {
            setPassword(e.target.value);
          }}
        />
        {/* Display the Forgot component with error message if 'show' is true */}
        <Invalid show={show} />
      </div>
      {/* "Remember me" checkbox */}
      <div className="check__Container">
        <input
          className="check__Container__Input"
          type="checkbox"
          id="remember"
          name="Remember me"
          onClick={handleCheck}
        />
        <label className="check__Container__Label" htmlFor="remember">
          Remember me
        </label>
      </div>
      {/* Sign-in button */}
      <div className="loginButton">
        <button className="button" type="button" onClick={handle_Form}>
          Sign in
        </button>
      </div>
    </form>
  );
}
