import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserCircle, faSignOut } from "@fortawesome/free-solid-svg-icons";
import Logo from "../../assets/argentBankLogo.png";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { remove_User } from "../../Redux/userReducer/userSlice.js";
import { remove_Auth } from "../../Redux/authReducer/authSlice.js";
import "../../sass/components/header/Header.scss";

export default function Header() {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const isAuth = useSelector((state) => state.login.authenticated);

  const firstName = useSelector((state) => state.user.firstName);
  const [fstName, setFstName] = useState(firstName);

  const [head, setHead] = useState({
    status: "notAuthenticated",
    signinBtn: {
      icon: faUserCircle,
      text: "Sign In",
    },
    profilBtn: {
      profileButtonClass: "hidden",
      icon: faUserCircle,
      IconClass: "hidden",
    },
  });

  useEffect(() => {
    if (isAuth) {
      setHead({
        status: "authenticated",
        signinBtn: {
          icon: faSignOut,
          text: "Sign Out",
        },
        profilBtn: {
          profileButtonClass: "header_Link",
          icon: faUserCircle,
          iconClass: "visible",
        },
      });
      setFstName(firstName);
    } else {
      setHead({
        status: "notAuthenticated",
        signinBtn: {
          icon: faUserCircle,
          text: "Sign In",
        },
        profilBtn: {
          profileButtonClass: "hidden",
          icon: faUserCircle,
          iconClass: "hidden",
        },
      });
    }
  }, [firstName, isAuth]);

  const HandleLogout = () => {
    // Gestion de la suppression de la conneixon
    dispatch(remove_User());
    dispatch(remove_Auth());
    localStorage.clear();

    navigate("/login");
  };

  return (
    <header className="header">
      <Link to="/" className="header__Link">
        <img src={Logo} alt="Argent Bank Logo" className="header__Logo" />
      </Link>
    </header>
  );
}
