import "../../sass/components/header/Nav.scss";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserCircle, faSignOut } from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { remove_User } from "../../Redux/userReducer/userSlice.js";
import { remove_Auth } from "../../Redux/authReducer/authSlice.js";

export default function Nav() {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const isAuth = useSelector((state) => state.login.authenticated);

  const firstName = useSelector((state) => state.user.firstName);
  const [firstNameState, setfirstNameState] = useState(firstName);

  const [head, setHead] = useState({
    status: "notAuthenticated",
    signinBtn: {
      icon: faUserCircle,
      text: "Sign In",
    },
    profilBtn: {
      profileButtonClass: "hidden",
      icon: faUserCircle,
      IconClass: "hidden",
    },
  });

  useEffect(() => {
    if (isAuth) {
      setHead({
        status: "authenticated",
        signinBtn: {
          icon: faSignOut,
          text: "Sign Out",
        },
        profilBtn: {
          profileButtonClass: "header__Link",
          icon: faUserCircle,
          iconClass: "visible",
        },
      });
      setfirstNameState(firstName);
    } else {
      setHead({
        status: "notAuthenticated",
        signinBtn: {
          icon: faUserCircle,
          text: "Sign In",
        },
        profilBtn: {
          profileButtonClass: "hidden",
          icon: faUserCircle,
          iconClass: "hidden",
        },
      });
    }
  }, [firstName, isAuth]);

  const HandleLogout = () => {
    // Gestion de la suppression de la conneixon
    dispatch(remove_User());
    dispatch(remove_Auth());
    localStorage.clear();

    navigate("/login");
  };
  return (
    <nav className="nav">
      <Link to="/profile" className={head.profilBtn.profileButtonClass}>
        <FontAwesomeIcon
          className={head.profilBtn.iconClass}
          icon={head.profilBtn.icon}
        />
        <p className="header__FirstName"> {firstNameState}</p>
      </Link>
      <Link to="/login" className="header__Link" onClick={HandleLogout}>
        <FontAwesomeIcon className="visible" icon={head.signinBtn.icon} />
        {head.signinBtn.text}
      </Link>
    </nav>
  );
}
