import { useEffect, useState } from "react";
import "../../../../sass/pages/userPage/TitleForm.scss";
import { put_UserNewInfos } from "../../../../services/login.service.js";
import { useDispatch, useSelector } from "react-redux";
import {
  set_FirstName,
  set_LastName,
} from "../../../../Redux/userReducer/userSlice.js";

export default function TitleForm({
  showSaveBtn,
  showCancelBtn,
  showEditBtn,
  showTitle,
  showForm,
  Edit_Btn,
  Cancel_Btn,
}) {
  // Get user data and token from Redux state
  const firstName = useSelector((state) => state.user.firstName);
  const lastName = useSelector((state) => state.user.lastName);
  const token = useSelector((state) => state.login.token);

  // Define state variables for first name and last name
  const [firstNameState, setfirstNameState] = useState(firstName);
  const [lstName, setLstName] = useState(lastName);
  const dispatch = useDispatch();

  // Function to handle updating the user's name
  async function handle_UpdateName(e) {
    e.preventDefault();

    // Call the API to update user information
    await put_UserNewInfos(token, firstNameState, lstName);

    // Dispatch updated first and last names to Redux store
    dispatch(set_FirstName(firstNameState));
    dispatch(set_LastName(lstName));

    // Call the Cancel_Btn function to hide the form and show the title
    Cancel_Btn();
  }

  // Update state when user data changes
  useEffect(() => {
    setfirstNameState(firstName);
    setLstName(lastName);
  }, [firstName, lastName]);

  // Determine CSS classes for various elements based on their visibility
  const saveBtn = showSaveBtn === "visible" ? "visible" : "hidden";
  const title = showTitle === "visible" ? "visible " : "hidden";
  const editBtn = showEditBtn === "visible" ? "visible" : "hidden";
  const cancelBtn = showCancelBtn === "visible" ? "visible" : "hidden";
  const form = showForm === "visible" ? "visible" : "hidden";

  return (
    <>
      {/* Title and User Name */}
      <h1 className="title">
        Welcome back
        {/* Display User Name */}
        <div id="userTitleName" className={`$"userTitleName" ${title}`}>
          <span className="firstName">{`${firstNameState} `}</span>
          <span className="lastName">{`${lstName}`}</span> !
        </div>
        {/* Form for Editing User Name */}
        <form id="userTitleForm" className={`$"userTitleForm" ${form}`}>
          {/* Input Fields for First Name and Last Name */}
          <div
            id="userTitleButtonContainer"
            className="userTitleInputContainer"
          >
            <input
              type="text"
              id="firstName"
              placeholder={fstName}
              className="userTitleInput"
              onChange={(e) => {
                if (e.target.value.trim() !== "") {
                  setFstName(e.target.value);
                }
              }}
            />
            <input
              type="text"
              id="lastName"
              placeholder={lstName}
              className="userTitleInput"
              onChange={(e) => {
                if (e.target.value.trim() !== "") {
                  setLstName(e.target.value);
                }
              }}
            />
          </div>
          {/* Buttons for Save and Cancel */}
          <div className="userTitleButtonContainer">
            <button
              key={Math.random()}
              className={`$"userTitleButton" ${saveBtn}`}
              type="submit"
              onClick={handle_UpdateName}
            >
              Save
            </button>
            <button
              key={Math.random()}
              className={`$"userTitleButton" ${cancelBtn}`}
              type="reset"
              onClick={Cancel_Btn}
            >
              Cancel
            </button>
          </div>
        </form>
      </h1>

      {/* Edit Button */}
      <div id="editBtnContainer" className={`${editBtn} $"editBtnContainer"`}>
        <button id="editBtn" className="button" onClick={Edit_Btn}>
          Edit Name
        </button>
      </div>
    </>
  );
}
